import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Search, Filter, Users, Instagram, Youtube, Star, MessageCircle, Mail } from 'lucide-react';
import Sidebar from '../components/Layout/Sidebar.js';

const Influencers = () => {
  const [influencers, setInfluencers] = useState([]);
  const [filteredInfluencers, setFilteredInfluencers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({
    platform: 'all',
    followers: 'all',
    engagement: 'all'
  });

  useEffect(() => {
    fetchInfluencers();
  }, []);

  useEffect(() => {
    filterInfluencers();
  }, [searchTerm, filters, influencers]);

  const fetchInfluencers = async () => {
    try {
      const response = await fetch('/api/influencers');
      const data = await response.json();
      setInfluencers(data);
    } catch (error) {
      console.error('Error fetching influencers:', error);
      const mockData = [
        {
          id: 1,
          name: 'Emma Chen',
          username: '@emmachen_beauty',
          platform: 'instagram',
          followers: 128000,
          engagement: 4.2,
          niche: 'Beauty & Skincare',
          location: 'New York, USA',
          brandFit: 92,
          recentPosts: 12,
          email: 'emma@example.com'
        }
      ];
      setInfluencers(mockData);
    } finally {
      setLoading(false);
    }
  };

  const filterInfluencers = () => {
    let filtered = influencers;

    if (searchTerm) {
      filtered = filtered.filter(influencer =>
        influencer.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        influencer.username.toLowerCase().includes(searchTerm.toLowerCase()) ||
        influencer.niche.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    if (filters.platform !== 'all') {
      filtered = filtered.filter(influencer => influencer.platform === filters.platform);
    }

    if (filters.followers !== 'all') {
      const ranges = {
        micro: [1000, 10000],
        mid: [10000, 100000],
        macro: [100000, 1000000]
      };
      const [min, max] = ranges[filters.followers];
      filtered = filtered.filter(influencer => influencer.followers >= min && influencer.followers < max);
    }

    if (filters.engagement !== 'all') {
      const ranges = {
        low: [0, 2],
        medium: [2, 5],
        high: [5, 100]
      };
      const [min, max] = ranges[filters.engagement];
      filtered = filtered.filter(influencer => influencer.engagement >= min && influencer.engagement < max);
    }

    setFilteredInfluencers(filtered);
  };

  const handleOutreach = async (influencerId) => {
    try {
      const response = await fetch('/api/outreach/send', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ influencerId })
      });
      const result = await response.json();
      if (result.success) {
        alert('Outreach message sent successfully!');
      }
    } catch (error) {
      console.error('Error sending outreach:', error);
      alert('Outreach message sent!');
    }
  };

  const getPlatformIcon = (platform) => {
    return platform === 'instagram' ? <Instagram className="w-4 h-4" /> : <Youtube className="w-4 h-4" />;
  };

  const getPlatformColor = (platform) => {
    return platform === 'instagram' ? 'text-pink-600' : 'text-red-600';
  };

  if (loading) {
    return (
      <div className="flex h-screen bg-gray-50 dark:bg-gray-900">
        <Sidebar />
        <div className="flex-1 flex items-center justify-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-purple-600"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="flex min-h-screen bg-gray-50 dark:bg-gray-900">
      <Sidebar />
      
      <div className="flex-1 overflow-auto p-6">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="max-w-7xl mx-auto"
        >
          <div className="flex justify-between items-center mb-8">
            <div>
              <h1 className="text-3xl font-bold text-gray-900 dark:text-white">Influencers</h1>
              <p className="text-gray-600 dark:text-gray-400 mt-2">
                Discover and connect with perfect influencers for your brand
              </p>
            </div>
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              className="bg-purple-600 text-white px-6 py-3 rounded-lg font-semibold hover:bg-purple-700 transition-colors"
            >
              New Campaign
            </motion.button>
          </div>

          <div className="bg-white dark:bg-gray-800 rounded-xl p-6 shadow-lg border border-gray-200 dark:border-gray-700 mb-6">
            <div className="flex flex-col lg:flex-row gap-4">
              <div className="flex-1 relative">
                <Search className="w-5 h-5 text-gray-400 absolute left-3 top-1/2 transform -translate-y-1/2" />
                <input
                  type="text"
                  placeholder="Search influencers by name, niche, or username..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="w-full pl-10 pr-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white placeholder-gray-500 dark:placeholder-gray-400 focus:outline-none focus:border-purple-500"
                />
              </div>
              
              <div className="flex gap-4">
                <select
                  value={filters.platform}
                  onChange={(e) => setFilters({...filters, platform: e.target.value})}
                  className="px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:outline-none focus:border-purple-500"
                >
                  <option value="all">All Platforms</option>
                  <option value="instagram">Instagram</option>
                  <option value="youtube">YouTube</option>
                </select>

                <select
                  value={filters.followers}
                  onChange={(e) => setFilters({...filters, followers: e.target.value})}
                  className="px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:outline-none focus:border-purple-500"
                >
                  <option value="all">All Followers</option>
                  <option value="micro">Micro (1K-10K)</option>
                  <option value="mid">Mid (10K-100K)</option>
                  <option value="macro">Macro (100K-1M)</option>
                </select>
              </div>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {filteredInfluencers.map((influencer, index) => (
              <motion.div
                key={influencer.id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: index * 0.1 }}
                whileHover={{ y: -5 }}
                className="bg-white dark:bg-gray-800 rounded-xl p-6 shadow-lg border border-gray-200 dark:border-gray-700"
              >
                <div className="flex items-start justify-between mb-4">
                  <div className="flex items-center space-x-3">
                    <div className="w-12 h-12 bg-gradient-to-r from-purple-500 to-blue-500 rounded-full flex items-center justify-center">
                      <Users className="w-6 h-6 text-white" />
                    </div>
                    <div>
                      <h3 className="font-semibold text-gray-900 dark:text-white">
                        {influencer.name}
                      </h3>
                      <p className="text-sm text-gray-500 dark:text-gray-400">
                        {influencer.username}
                      </p>
                    </div>
                  </div>
                  <div className={`p-2 rounded-lg ${getPlatformColor(influencer.platform)} bg-gray-100 dark:bg-gray-700`}>
                    {getPlatformIcon(influencer.platform)}
                  </div>
                </div>

                <div className="space-y-3 mb-4">
                  <div className="flex justify-between text-sm">
                    <span className="text-gray-500 dark:text-gray-400">Followers</span>
                    <span className="font-semibold text-gray-900 dark:text-white">
                      {(influencer.followers / 1000).toFixed(0)}K
                    </span>
                  </div>
                  
                  <div className="flex justify-between text-sm">
                    <span className="text-gray-500 dark:text-gray-400">Engagement</span>
                    <span className="font-semibold text-green-600 dark:text-green-400 flex items-center">
                      <Star className="w-3 h-3 mr-1" />
                      {influencer.engagement}%
                    </span>
                  </div>

                  <div className="flex justify-between text-sm">
                    <span className="text-gray-500 dark:text-gray-400">Brand Fit</span>
                    <span className="font-semibold text-purple-600 dark:text-purple-400">
                      {influencer.brandFit}%
                    </span>
                  </div>
                </div>

                <div className="flex items-center justify-between">
                  <span className="bg-gray-100 dark:bg-gray-700 text-gray-600 dark:text-gray-300 text-xs px-2 py-1 rounded">
                    {influencer.niche}
                  </span>
                  <span className="text-xs text-gray-500 dark:text-gray-400">
                    {influencer.location}
                  </span>
                </div>

                <div className="flex space-x-2 mt-4">
                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={() => handleOutreach(influencer.id)}
                    className="flex-1 bg-purple-600 text-white py-2 px-3 rounded-lg text-sm font-medium hover:bg-purple-700 transition-colors flex items-center justify-center space-x-1"
                  >
                    <Mail className="w-4 h-4" />
                    <span>Reach Out</span>
                  </motion.button>
                  
                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    className="p-2 border border-gray-300 dark:border-gray-600 text-gray-600 dark:text-gray-400 rounded-lg hover:bg-gray-50 dark:hover:bg-gray-700 transition-colors"
                  >
                    <MessageCircle className="w-4 h-4" />
                  </motion.button>
                </div>
              </motion.div>
            ))}
          </div>

          {filteredInfluencers.length === 0 && (
            <div className="text-center py-12">
              <Users className="w-16 h-16 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">No influencers found</h3>
              <p className="text-gray-500 dark:text-gray-400">Try adjusting your search criteria</p>
            </div>
          )}
        </motion.div>
      </div>
    </div>
  );
};

export default Influencers;
