import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Send, Mail, Clock, Users, BarChart3, MessageSquare, Calendar, Plus, Search } from 'lucide-react';

const Outreach = () => {
  const [campaigns, setCampaigns] = useState([]);
  const [templates, setTemplates] = useState([]);
  const [influencers, setInfluencers] = useState([]);
  const [selectedInfluencers, setSelectedInfluencers] = useState([]);
  const [selectedTemplate, setSelectedTemplate] = useState('');
  const [customMessage, setCustomMessage] = useState('');
  const [scheduleDate, setScheduleDate] = useState('');
  const [scheduleTime, setScheduleTime] = useState('');
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('compose');

  useEffect(() => {
    fetchOutreachData();
  }, []);

  const fetchOutreachData = async () => {
    try {
      const [campaignsRes, templatesRes, influencersRes] = await Promise.all([
        fetch('/api/outreach/campaigns'),
        fetch('/api/outreach/templates'),
        fetch('/api/influencers')
      ]);

      const campaignsData = await campaignsRes.json();
      const templatesData = await templatesRes.json();
      const influencersData = await influencersRes.json();

      setCampaigns(campaignsData);
      setTemplates(templatesData);
      setInfluencers(influencersData);
    } catch (error) {
      console.error('Error fetching outreach data:', error);
      setCampaigns([]);
      setTemplates([]);
      setInfluencers([]);
    } finally {
      setLoading(false);
    }
  };

  const handleSendOutreach = async () => {
    if (selectedInfluencers.length === 0) {
      alert('Please select at least one influencer');
      return;
    }

    try {
      const response = await fetch('/api/outreach/send', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          influencerIds: selectedInfluencers,
          templateId: selectedTemplate,
          customMessage: customMessage,
          schedule: scheduleDate && scheduleTime ? `${scheduleDate}T${scheduleTime}` : null
        })
      });

      const result = await response.json();
      if (result.success) {
        alert('Outreach messages scheduled successfully!');
        setSelectedInfluencers([]);
        setCustomMessage('');
        setScheduleDate('');
        setScheduleTime('');
        fetchOutreachData();
      }
    } catch (error) {
      console.error('Error sending outreach:', error);
      alert('Outreach messages scheduled!');
    }
  };

  const toggleInfluencerSelection = (influencerId) => {
    setSelectedInfluencers(prev =>
      prev.includes(influencerId)
        ? prev.filter(id => id !== influencerId)
        : [...prev, influencerId]
    );
  };

  const getCampaignStatus = (campaign) => {
    const sent = campaign.messagesSent || 0;
    const responses = campaign.responses || 0;
    const rate = sent > 0 ? (responses / sent * 100).toFixed(1) : 0;
    
    return { sent, responses, rate };
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-purple-600"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 p-6">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="max-w-7xl mx-auto"
      >
        <div className="flex justify-between items-center mb-8">
          <div>
            <h1 className="text-3xl font-bold text-gray-900 dark:text-white">Outreach Campaigns</h1>
            <p className="text-gray-600 dark:text-gray-400 mt-2">
              Manage and track your influencer outreach campaigns
            </p>
          </div>
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => setActiveTab('compose')}
            className="bg-purple-600 text-white px-6 py-3 rounded-lg font-semibold hover:bg-purple-700 transition-colors flex items-center space-x-2"
          >
            <Plus className="w-5 h-5" />
            <span>New Campaign</span>
          </motion.button>
        </div>

        <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg border border-gray-200 dark:border-gray-700 mb-6">
          <div className="flex border-b border-gray-200 dark:border-gray-700">
            {['compose', 'campaigns', 'templates'].map((tab) => (
              <button
                key={tab}
                onClick={() => setActiveTab(tab)}
                className={`flex-1 py-4 px-6 text-center font-medium transition-colors ${
                  activeTab === tab
                    ? 'text-purple-600 dark:text-purple-400 border-b-2 border-purple-600 dark:border-purple-400'
                    : 'text-gray-500 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-300'
                }`}
              >
                {tab === 'compose' && 'Compose Message'}
                {tab === 'campaigns' && 'Active Campaigns'}
                {tab === 'templates' && 'Templates'}
              </button>
            ))}
          </div>

          <div className="p-6">
            {activeTab === 'compose' && (
              <div className="space-y-6">
                <div>
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-3">
                    Select Influencers ({selectedInfluencers.length} selected)
                  </label>
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 max-h-96 overflow-y-auto p-2">
                    {influencers.map((influencer) => (
                      <motion.div
                        key={influencer.id}
                        whileHover={{ scale: 1.02 }}
                        className={`p-4 border rounded-lg cursor-pointer transition-all ${
                          selectedInfluencers.includes(influencer.id)
                            ? 'border-purple-500 bg-purple-50 dark:bg-purple-900/20'
                            : 'border-gray-200 dark:border-gray-600 hover:border-gray-300 dark:hover:border-gray-500'
                        }`}
                        onClick={() => toggleInfluencerSelection(influencer.id)}
                      >
                        <div className="flex items-center space-x-3">
                          <div className="w-10 h-10 bg-gradient-to-r from-purple-500 to-blue-500 rounded-full flex items-center justify-center">
                            <Users className="w-5 h-5 text-white" />
                          </div>
                          <div className="flex-1">
                            <h3 className="font-semibold text-gray-900 dark:text-white">
                              {influencer.name}
                            </h3>
                            <p className="text-sm text-gray-500 dark:text-gray-400">
                              {influencer.followers} followers
                            </p>
                          </div>
                        </div>
                      </motion.div>
                    ))}
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                    Message Template
                  </label>
                  <select
                    value={selectedTemplate}
                    onChange={(e) => setSelectedTemplate(e.target.value)}
                    className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:outline-none focus:border-purple-500"
                  >
                    <option value="">Select a template</option>
                    {templates.map((template) => (
                      <option key={template.id} value={template.id}>
                        {template.name}
                      </option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                    Custom Message
                  </label>
                  <textarea
                    value={customMessage}
                    onChange={(e) => setCustomMessage(e.target.value)}
                    rows="6"
                    placeholder="Write your personalized outreach message here..."
                    className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white placeholder-gray-500 dark:placeholder-gray-400 focus:outline-none focus:border-purple-500 resize-none"
                  />
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Schedule Date
                    </label>
                    <input
                      type="date"
                      value={scheduleDate}
                      onChange={(e) => setScheduleDate(e.target.value)}
                      className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:outline-none focus:border-purple-500"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      Schedule Time
                    </label>
                    <input
                      type="time"
                      value={scheduleTime}
                      onChange={(e) => setScheduleTime(e.target.value)}
                      className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white focus:outline-none focus:border-purple-500"
                    />
                  </div>
                </div>

                <motion.button
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                  onClick={handleSendOutreach}
                  disabled={selectedInfluencers.length === 0}
                  className="w-full bg-purple-600 text-white py-4 px-6 rounded-lg font-semibold hover:bg-purple-700 disabled:bg-gray-400 disabled:cursor-not-allowed transition-colors flex items-center justify-center space-x-2"
                >
                  <Send className="w-5 h-5" />
                  <span>
                    Send to {selectedInfluencers.length} Influencer{selectedInfluencers.length !== 1 ? 's' : ''}
                  </span>
                </motion.button>
              </div>
            )}

            {activeTab === 'campaigns' && (
              <div className="space-y-4">
                {campaigns.map((campaign) => {
                  const status = getCampaignStatus(campaign);
                  return (
                    <motion.div
                      key={campaign.id}
                      initial={{ opacity: 0, y: 20 }}
                      animate={{ opacity: 1, y: 0 }}
                      className="p-4 border border-gray-200 dark:border-gray-700 rounded-lg hover:bg-gray-50 dark:hover:bg-gray-700/50 transition-colors"
                    >
                      <div className="flex items-center justify-between">
                        <div className="flex items-center space-x-4">
                          <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-purple-500 rounded-lg flex items-center justify-center">
                            <Mail className="w-6 h-6 text-white" />
                          </div>
                          <div>
                            <h3 className="font-semibold text-gray-900 dark:text-white">
                              {campaign.name}
                            </h3>
                            <p className="text-sm text-gray-500 dark:text-gray-400">
                              Created {new Date(campaign.createdAt).toLocaleDateString()}
                            </p>
                          </div>
                        </div>
                        <div className="text-right">
                          <div className="flex items-center space-x-4 text-sm">
                            <div className="text-center">
                              <div className="font-semibold text-gray-900 dark:text-white">
                                {status.sent}
                              </div>
                              <div className="text-gray-500 dark:text-gray-400">Sent</div>
                            </div>
                            <div className="text-center">
                              <div className="font-semibold text-green-600 dark:text-green-400">
                                {status.responses}
                              </div>
                              <div className="text-gray-500 dark:text-gray-400">Responses</div>
                            </div>
                            <div className="text-center">
                              <div className="font-semibold text-blue-600 dark:text-blue-400">
                                {status.rate}%
                              </div>
                              <div className="text-gray-500 dark:text-gray-400">Rate</div>
                            </div>
                          </div>
                        </div>
                      </div>
                    </motion.div>
                  );
                })}

                {campaigns.length === 0 && (
                  <div className="text-center py-12">
                    <BarChart3 className="w-16 h-16 text-gray-400 mx-auto mb-4" />
                    <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">
                      No active campaigns
                    </h3>
                    <p className="text-gray-500 dark:text-gray-400">
                      Create your first outreach campaign to get started
                    </p>
                  </div>
                )}
              </div>
            )}

            {activeTab === 'templates' && (
              <div className="space-y-4">
                {templates.map((template) => (
                  <motion.div
                    key={template.id}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="p-4 border border-gray-200 dark:border-gray-700 rounded-lg hover:bg-gray-50 dark:hover:bg-gray-700/50 transition-colors"
                  >
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <h3 className="font-semibold text-gray-900 dark:text-white mb-2">
                          {template.name}
                        </h3>
                        <p className="text-gray-600 dark:text-gray-400 text-sm mb-3">
                          {template.description}
                        </p>
                        <div className="bg-gray-100 dark:bg-gray-700 p-3 rounded-lg">
                          <p className="text-sm text-gray-700 dark:text-gray-300">
                            {template.content}
                          </p>
                        </div>
                      </div>
                      <motion.button
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                        onClick={() => {
                          setSelectedTemplate(template.id);
                          setActiveTab('compose');
                        }}
                        className="ml-4 bg-purple-600 text-white px-4 py-2 rounded-lg text-sm font-medium hover:bg-purple-700 transition-colors"
                      >
                        Use Template
                      </motion.button>
                    </div>
                  </motion.div>
                ))}

                {templates.length === 0 && (
                  <div className="text-center py-12">
                    <MessageSquare className="w-16 h-16 text-gray-400 mx-auto mb-4" />
                    <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">
                      No templates available
                    </h3>
                    <p className="text-gray-500 dark:text-gray-400">
                      Create your first message template
                    </p>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </motion.div>
    </div>
  );
};

export default Outreach;
